import React, { useState, useEffect } from 'react';
import './index.css';
import axios from 'axios';
import './App.css';
import Dropdown from './Dropdown';

function Square({ value, onClick }) {
  return (
    <button className='square' onClick={onClick}>
      {value}
    </button>
  );
}

function Restart({ onClick }) {
  return (
    <button className='restart' onClick={onClick}>
      Refresh
    </button>
  );
}

function StartGame({ title, onClick, disabled }) {
  return (
    <button className='restart' onClick={onClick} disabled={disabled}>
      {title}
    </button>
  );
}
function App() {
  const [squares, setSquares] = useState(Array(9).fill(null));
  const [isXNext, setIsXNext] = useState(true);
  const [isActiveGame, setActiveGame] = useState(false);
  const [currMove, setCurrMove] = useState([]);
  const [selectedGame, setSelectedGame] = useState(0);
  const [label, setLabel] = useState('---------');
  const nextSymbol = isXNext ? 'X' : 'O';

  const winner = calculateWinner(squares);

  function renderSquare(i) {
    // console.log(i, 'ii');
    return (
      <Square
        value={squares[i]}
        onClick={() => {
          if (selectedGame == 1) {
            if (squares[i] != null || winner != null) {
              return;
            }
            const nextSquares = squares.slice();
            nextSquares[i] = isXNext ? 'X' : 'O';
            setSquares(nextSquares);

            setIsXNext(!isXNext); // toggle turns
          }
        }}
      />
    );
  }
  function calculateWinner(squares) {
    const possibleLines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];
    // go over all possibly winning lines and check if they consist of only X's/only O's
    for (let i = 0; i < possibleLines.length; i++) {
      const [a, b, c] = possibleLines[i];
      if (
        squares[a] &&
        squares[a] === squares[b] &&
        squares[a] === squares[c]
      ) {
        return squares[a];
      }
    }
    return null;
  }

  function isBoardFull(squares) {
    for (let i = 0; i < squares.length; i++) {
      if (squares[i] == null) {
        return false;
      }
    }
    return true;
  }

  function getStatus() {
    if (winner) {
      console.log(winner, 'wiwi');
      return 'Winner: ' + winner + ' !!!!!!';
    } else if (isBoardFull(squares)) {
      return 'Draw!';
    } else {
      return 'Next player: ' + (isXNext ? 'X' : 'O');
    }
  }

  function renderRestartButton() {
    return (
      <Restart
        onClick={() => {
          setSquares(Array(9).fill(null));
          setIsXNext(true);
          setActiveGame(false);
          setLabel('---------');
        }}
      />
    );
  }
  console.log('winner', winner);

  function renderstartButton() {
    return (
      <StartGame
        title={isActiveGame ? 'Next Move' : 'Start Game'}
        disabled={winner || isBoardFull(squares) ? true : false}
        onClick={async () => {
          let j = [];
          console.log(label, 'label000');
          let k = `http://localhost:8080/menace/next/${label}/${nextSymbol}`;
          console.log(k, 'sllslsl');
          setActiveGame(true);
          await axios
            .get(k)
            .then((res) => {
              console.log(res.data, 'sksksk');
              j = [...res.data];
            })
            .catch((e) => console.log(e, 'error'));
          console.log(j, 'jjj');
          j.map((item) => {
            console.log(item, 'u');
            if (squares[item.cellNumber - 1] != null || winner != null) {
              return;
            }
            const nextSquares = squares.slice();
            nextSquares[item.cellNumber - 1] = item.player;
            let test = [];
            // console.log(nextSquares, "nextSquares");
            nextSquares.map((i) => {
              if (i == null) {
                test.push('-');
              } else {
                test.push(i);
              }
            });

            setLabel(test.join(''));

            console.log(test.join(''), 'test');

            setSquares(nextSquares);

            setIsXNext(!isXNext); // toggle turns
            renderSquare(item.cellNumber - 1);
          });
          // setSquares(Array(9).fill(null));
          // setIsXNext(true);
        }}
      />
    );
  }

  function getCurrentGame(selectedGame) {
    console.log(selectedGame, 'selectedGame');
    setSelectedGame(selectedGame);
  }

  return (
    <div className='container'>
      <h3> MENACE - a Machine Educable Noughts And Crosses Engine </h3>
      <Dropdown getCurrentGame={getCurrentGame} />
      <div className='game'>
        <div className='game-board'>
          <div className='board-row'>
            {renderSquare(0)}
            {renderSquare(1)}
            {renderSquare(2)}
          </div>
          <div className='board-row'>
            {renderSquare(3)}
            {renderSquare(4)}
            {renderSquare(5)}
          </div>
          <div className='board-row'>
            {renderSquare(6)}
            {renderSquare(7)}
            {renderSquare(8)}
          </div>
        </div>
        <div className={!winner ? 'game-info' : 'win-state'}>{getStatus()}</div>
        <div className='wrapper'>
          <div className='restart-button'>{renderRestartButton()}</div>
          <div className='restart-button'>{renderstartButton()}</div>
        </div>
      </div>
    </div>
  );
}

export default App;
